select

from daoxin_contacts where 1 = 1

            and contacts_id = #{paramCondition.contactsId}


        and name like CONCAT('%',#{paramCondition.name},'%')


        and job like CONCAT('%',#{paramCondition.job},'%')


            and phone = #{paramCondition.phone}

