SELECT daoxin_erp_parts_detail.parts_id as partId, daoxin_erp_parts_detail.number as number, goods_sku.sku_id as skuId, daoxin_erp_parts_detail.create_time as createTime FROM daoxin_erp_parts_detail RIGHT JOIN (SELECT * FROM goods_sku WHERE display = 1) AS goods_sku ON goods_sku.sku_id = daoxin_erp_parts_detail.sku_id where 1=1
and partsId = #{paramCondition.partsId}
and goods_sku.sku_id in
#{state}

