mapper.select:
select

from daoxin_erp_instock where display = 1

            and item_id = #{paramCondition.itemId}


        and Storehouse_id = #{paramCondition.StorehouseId}


        and brand_id = #{paramCondition.brandId}


        and barcode = #{paramCondition.barcode}


        and state = #{paramCondition.state}

select

from daoxin_erp_instock where display = 1

            and item_id = #{paramCondition.itemId}


            and Storehouse_id = #{paramCondition.StorehouseId}


            and brand_id = #{paramCondition.brandId}


            and barcode = #{paramCondition.barcode}


            and state = #{paramCondition.state}

select

from daoxin_erp_instock where display = 1

    and item_id = #{paramCondition.itemId}


        and register_time like CONCAT('%',#{paramCondition.registerTime},'%')


        and Storehouse_id = #{paramCondition.StorehouseId}


        and brand_id = #{paramCondition.brandId}


        and barcode = #{paramCondition.barcode}


        and state = #{paramCondition.state}

select

from daoxin_erp_instock where display = 1

                and item_id = #{paramCondition.itemId}


            and Storehouse_id = #{paramCondition.StorehouseId}


            and brand_id = #{paramCondition.brandId}


            and barcode = #{paramCondition.barcode}


            and state = #{paramCondition.state}
