mapper.select:
select


from daoxin_erp_stock_details where 1 = 1

    and stock_id = #{paramCondition.stockId}


    and storehouse_id = #{paramCondition.storehouseId}


    and brand_id = #{paramCondition.brandId}

select

from daoxin_erp_stock_details where 1 = 1

    and stock_id = #{paramCondition.stockId}


    and item_id = #{paramCondition.itemId}


    and storehouse_id = #{paramCondition.storehouseId}


    and brand_id = #{paramCondition.brandId}

select

from daoxin_erp_stock_details where display = 1 and number != 0

    and stock_id = #{paramCondition.stockId}


    and sku_id = #{paramCondition.skuId}


    and storage_time like CONCAT('%',#{paramCondition.storageTime},'%')


    and inkind_id in (
    select form_id from qr_code_bind where display = 1 and SUBSTRING(qr_code_id, -6) like
    CONCAT('%',#{paramCondition.qrCodeid},'%') and source = 'item'
    )


    and storehouse_id = #{paramCondition.storehouseId}


    and brand_id = #{paramCondition.brandId}


    and outstock_order_id = #{paramCondition.outStockOrderId}


    and storehouse_positions_id = #{paramCondition.storehousePositionsId}


    and stage = #{paramCondition.stage}


    and sku_id in (SELECT sku_id from goods_sku where spu_id in (select spu_id from goods_spu where name like
    CONCAT
    ('%',#{paramCondition.skuName},'%') ) )
    UNION
    SELECT

    FROM daoxin_erp_stock_details where sku_id in (SELECT sku_id from goods_sku where sku_name like CONCAT
    ('%',#{paramCondition.skuName},'%') )


    and deptId = #{paramCondition.deptId} or deptId in ( select dept_id from sys_dept where pids like
    CONCAT('%$[', #{paramCondition.deptId}, '$]%') escape '$' ))


    and inkind_id in

        #{state}



    and inkind_id like CONCAT('%',#{paramCondition.inkindId},'%')


    and inkind_id not in

        #{state}




        and inkind_id in (
        select inkind_id from goods_inkind where anomaly = 1
        )


        and inkind_id in (
        select inkind_id from goods_inkind where anomaly = 0
        )

select


from daoxin_erp_stock_details where 1 = 1

    and stock_id = #{paramCondition.stockId}


    and item_id = #{paramCondition.itemId}


    and storehouse_id = #{paramCondition.storehouseId}


    and brand_id = #{paramCondition.brandId}

select

from daoxin_erp_stock_details where display = 1 and number != 0 and stage = 1


    and storehouse_id = #{paramCondition.storehouseId}


    and brand_id = #{paramCondition.brandId}


    and storehouse_positions_id = #{paramCondition.storehousePositionsId}


    and sku_id in

        #{state}



    and brand_id in

        #{state}



    and inkind_id not in

        #{notNeedMaintenanceInkindId}



    and storehouse_positions_id in

        #{positionId}

select sum(number) as number
from daoxin_erp_stock_details where display = 1

    and sku_id = #{skuId}


    and brand_id =#{brandId}


    and storehouse_positions_id = #{positionId}

group by


        sku_id,


        brand_id ,


        storehouse_positions_id ,

SELECT sum( detail.number ) AS "number", class.`name` AS "className", material.`name` AS "materialName", storehouse.`name` AS "storehouseName", class.spu_classification_id AS "spuClassId", material.material_id AS "materialId", storehouse.storehouse_id AS "storehouseId", customer.customer_id AS "customerId", customer.customer_name AS "customerName", count( DISTINCT detail.sku_id ) AS "skuCount", detail.create_time AS "createTime" FROM daoxin_erp_stock_details detail LEFT JOIN goods_inkind inkind ON detail.inkind_id = inkind.inkind_id LEFT JOIN goods_sku sku on detail.sku_id = sku.sku_id LEFT JOIN goods_spu spu on sku.spu_id = spu.spu_id LEFT JOIN goods_spu_class class on class.spu_classification_id = spu.spu_classification_id LEFT JOIN daoxin_erp_material material on sku.material_id = material.material_id LEFT JOIN daoxin_erp_storehouse storehouse on storehouse.storehouse_id = detail.storehouse_id LEFT JOIN daoxin_crm_customer customer on inkind.customer_id = customer.customer_id WHERE detail.display = 1 and detail.stage = 1
and spu.spu_classification_id = #{paramCondition.spuClassId}
and material.material_id = #{paramCondition.materialId}
and customer.customer_id = #{paramCondition.customerId}
and storehouse.storehouse_id = #{paramCondition.storehouseId}
GROUP BY spu.spu_classification_id
GROUP BY storehouse.storehouse_id
GROUP BY material.material_id
GROUP BY customer.customer_id
ORDER BY sum( detail.number ) DESC

SELECT sum( detail.number ) AS "number", detail.sku_id AS "skuId", class.spu_classification_id AS "spuClassId", material.material_id AS "materialId", storehouse.storehouse_id AS "storehouseId", customer.customer_id AS "customerId", detail.create_time AS "createTime" FROM daoxin_erp_stock_details detail LEFT JOIN goods_inkind inkind ON detail.inkind_id = inkind.inkind_id LEFT JOIN goods_sku sku on detail.sku_id = sku.sku_id LEFT JOIN goods_spu spu on sku.spu_id = spu.spu_id LEFT JOIN goods_spu_class class on class.spu_classification_id = spu.spu_classification_id LEFT JOIN daoxin_erp_material material on sku.material_id = material.material_id LEFT JOIN daoxin_erp_storehouse storehouse on storehouse.storehouse_id = detail.storehouse_id LEFT JOIN daoxin_crm_customer customer on inkind.customer_id = customer.customer_id WHERE detail.display = 1 and detail.stage = 1
and spu.spu_classification_id = #{paramCondition.spuClassId}
and material.material_id = #{paramCondition.materialId}
and customer.customer_id = #{paramCondition.customerId}
and storehouse.storehouse_id = #{paramCondition.storehouseId}
GROUP BY detail.sku_id ORDER BY sum( detail.number ) DESC

SELECT sum( detail.number ) AS "number", class.`name` AS "className", class.spu_classification_id AS "spuClassId", material.material_id AS "materialId", storehouse.storehouse_id AS "storehouseId", customer.customer_id AS "customerId", material.`name` AS "materialName", storehouse.`name` AS "storehouseName", customer.customer_name AS "customerName", count( DISTINCT detail.sku_id ) AS "skuCount", detail.create_time AS "createTime" FROM daoxin_erp_stock_details detail LEFT JOIN goods_inkind inkind ON detail.inkind_id = inkind.inkind_id LEFT JOIN goods_sku sku on detail.sku_id = sku.sku_id LEFT JOIN goods_spu spu on sku.spu_id = spu.spu_id LEFT JOIN goods_spu_class class on class.spu_classification_id = spu.spu_classification_id LEFT JOIN daoxin_erp_material material on sku.material_id = material.material_id LEFT JOIN daoxin_erp_storehouse storehouse on storehouse.storehouse_id = detail.storehouse_id LEFT JOIN daoxin_crm_customer customer on inkind.customer_id = customer.customer_id WHERE detail.display = 1 and detail.stage = 1
AND detail.create_time >= DATE_SUB( CURDATE(), INTERVAL 1 MONTH )
AND detail.create_time <= DATE_SUB( CURDATE(), INTERVAL 1 MONTH ) AND detail.create_time >= DATE_SUB( CURDATE(), INTERVAL 3 MONTH )
AND detail.create_time <= DATE_SUB( CURDATE(), INTERVAL 3 MONTH ) AND detail.create_time >= DATE_SUB( CURDATE(), INTERVAL 6 MONTH )
AND detail.create_time <= DATE_SUB( CURDATE(), INTERVAL 6 MONTH )
and spu.spu_classification_id = #{paramCondition.spuClassId}
and material.material_id = #{paramCondition.materialId}
and customer.customer_id = #{paramCondition.customerId}
and storehouse.storehouse_id = #{paramCondition.storehouseId}

SELECT sum( detail.number ) AS "number", detail.sku_id AS "skuId", detail.create_time AS "createTime" FROM daoxin_erp_stock_details detail LEFT JOIN goods_inkind inkind ON detail.inkind_id = inkind.inkind_id LEFT JOIN goods_sku sku on detail.sku_id = sku.sku_id LEFT JOIN goods_spu spu on sku.spu_id = spu.spu_id LEFT JOIN goods_spu_class class on class.spu_classification_id = spu.spu_classification_id LEFT JOIN daoxin_erp_material material on sku.material_id = material.material_id LEFT JOIN daoxin_erp_storehouse storehouse on storehouse.storehouse_id = detail.storehouse_id LEFT JOIN daoxin_crm_customer customer on inkind.customer_id = customer.customer_id WHERE detail.display = 1 and detail.stage = 1
AND detail.create_time >= DATE_SUB( CURDATE(), INTERVAL 1 MONTH )
AND detail.create_time <= DATE_SUB( CURDATE(), INTERVAL 1 MONTH ) AND detail.create_time >= DATE_SUB( CURDATE(), INTERVAL 3 MONTH )
AND detail.create_time <= DATE_SUB( CURDATE(), INTERVAL 3 MONTH ) AND detail.create_time >= DATE_SUB( CURDATE(), INTERVAL 6 MONTH )
AND detail.create_time <= DATE_SUB( CURDATE(), INTERVAL 6 MONTH )
and spu.spu_classification_id = #{paramCondition.spuClassId}
and material.material_id = #{paramCondition.materialId}
and customer.customer_id = #{paramCondition.customerId}
and storehouse.storehouse_id = #{paramCondition.storehouseId}
GROUP BY detail.sku_id ORDER BY sum( detail.number )

SELECT IF(SUM(number) is null , 0 , SUM(number)) AS number,sku_id AS "skuId" FROM `daoxin_erp_stock_details` WHERE 1 = 1 and display = 1 and stage = 1 and sku_id in
#{skuId}
group by sku_id

SELECT IF(SUM(number) is null , 0 , SUM(number)) AS number,sku_id AS "skuId",brand_id AS "brandId",customer_id AS "customerId",storehouse_positions_id AS "storehousePositionsId" FROM `daoxin_erp_stock_details` WHERE 1 = 1 and display = 1 and stage = 1 and
sku_id = #{item.skuId} and brand_id = #{item.brandId} and storehouse_positions_id = #{item.positionId}
and customer_id = #{item.customerId}
group by sku_id,customer_id,brand_id,storehouse_positions_id

SELECT stock_item_id AS "stockItemId",number AS number,inkind_id AS inkindId,sku_id AS "skuId",brand_id AS "brandId",customer_id AS "customerId",storehouse_positions_id AS "storehousePositionsId" FROM `daoxin_erp_stock_details` WHERE 1 = 1 and display = 1 and stage = 1 and
sku_id = #{item.skuId} and brand_id = #{item.brandId} and storehouse_positions_id = #{item.positionId}
and customer_id = #{item.customerId}
order by create_time
