mapper.select:
SELECT
FROM daoxin_production_pick_lists_cart c LEFT JOIN daoxin_production_pick_lists l on c.pick_lists_id = l.pick_lists_id LEFT JOIN goods_sku s on s.sku_id = c.sku_id LEFT JOIN goods_spu p on p.spu_id = s.spu_id WHERE 1=1 and c.display = 1
and ( s.sku_name like CONCAT('%',#{paramCondition.query},'%') or p.name like CONCAT('%',#{paramCondition.query},'%') or l.coding like CONCAT('%',#{paramCondition.query},'%') )
and l.user_id = #{userId}
and c.pick_lists_id = #{paramCondition.pickListsId}

select
sum(number) AS number
from daoxin_production_pick_lists_cart where 1 = 1 and display = 1 and status = 0

    and storehouse_positions_id = #{paramCondition.positionId}


    and brand_id = #{paramCondition.brandId}


    and sku_id = #{paramCondition.skuId}
