mapper.select:
select
FROM `daoxin_production_pick_lists_detail` a LEFT JOIN daoxin_erp_stock_details b ON a.sku_id = b.sku_id AND a.brand_id = b.brand_id WHERE 1 = 1 and a.display = 1
and b.storehouse_positions_id = #{paramCondition.storehousePositionsId}
and a.pick_lists_id = #{paramCondition.pickListsId}
UNION select
FROM `daoxin_production_pick_lists_detail` a LEFT JOIN daoxin_erp_stock_details b ON a.sku_id = b.sku_id WHERE 1 = 1 and a.display = 1 and a.brand_id = 0
and b.storehouse_positions_id = #{paramCondition.storehousePositionsId}
and a.pick_lists_id = #{paramCondition.pickListsId}

select
FROM `daoxin_production_pick_lists_detail` a LEFT JOIN daoxin_production_pick_lists_cart b ON a.pick_lists_id = b.pick_lists_id and a.sku_id = b.sku_id WHERE b.sku_id is NULL
and a.status = #{paramCondition.status}
