mapper.select:
select
from activiti_process_task LEFT JOIN daoxin_production_pick_lists on activiti_process_task.form_id = daoxin_production_pick_lists.pick_lists_id where 1 = 1 and activiti_process_task.display = 1
and daoxin_production_pick_lists.user_id = #{paramCondition.userId}
and activiti_process_task.type = #{paramCondition.type}

select

from activiti_process_task
LEFT JOIN daoxin_task_participant ON activiti_process_task.process_task_id =
daoxin_task_participant.process_task_id
where 1 = 1 and activiti_process_task.display = 1

    and activiti_process_task.create_user = #{paramCondition.createUser}


    and activiti_process_task.type = #{paramCondition.type}


    and activiti_process_task.status in

        #{state}



    and activiti_process_task.type in

        #{state}



    and activiti_process_task.process_task_id in

        #{state}



    and (activiti_process_task.form_id in (
    select instock_order_id from daoxin_erp_instock_order where coding like
    CONCAT('%',#{paramCondition.skuName},'%')
    union
    select order_id from daoxin_anomaly_order where coding like CONCAT('%',#{paramCondition.skuName},'%')
    union
    select inventory_task_id from daoxin_erp_inventory where coding like
    CONCAT('%',#{paramCondition.skuName},'%')
    union
    select pick_lists_id from daoxin_production_pick_lists where coding like
    CONCAT('%',#{paramCondition.skuName},'%')
    ) or task_name like CONCAT('%',#{paramCondition.skuName},'%')
    or remark like CONCAT('%',#{paramCondition.skuName},'%')
    or notice_id like CONCAT('%',#{paramCondition.skuName},'%')
    )


    and activiti_process_task.process_task_id in

        #{state}



    and (daoxin_task_participant.user_id = #{paramCondition.participantUser}
    or activiti_process_task.user_id = #{paramCondition.participantUser}
    )

group by processTaskId

    select

    from activiti_process_task task
    LEFT JOIN activiti_process_log  log on log.task_id = task.process_task_id
    LEFT JOIN activiti_steps steps  on steps.setps_id = log.setps_id
    LEFT JOIN daoxin_task_participant participant ON task.process_task_id =
    participant.process_task_id
    where  1 = 1  and task.display = 1
--  查询关于我的任务




                and steps.type = "audit"
                and log.audit_user_id = #{paramCondition.userId}

                    and log.status =  #{paramCondition.status}



                and steps.type = "send"
                and log.audit_user_id = #{paramCondition.userId}
                and log.status = 1







        and (
        task.form_id in (
        select instock_order_id from daoxin_erp_instock_order where coding like
        CONCAT('%',#{paramCondition.skuName},'%')
        union
        select order_id from daoxin_anomaly_order where coding like CONCAT('%',#{paramCondition.skuName},'%')
        union
        select inventory_task_id from daoxin_erp_inventory where coding like
        CONCAT('%',#{paramCondition.skuName},'%')
        union
        select pick_lists_id from daoxin_production_pick_lists where coding like
        CONCAT('%',#{paramCondition.skuName},'%')
        )
        or task_name like CONCAT('%',#{paramCondition.skuName},'%')
        or remark like CONCAT('%',#{paramCondition.skuName},'%')
        or notice_id like CONCAT('%',#{paramCondition.skuName},'%')
        )


        and task.create_user = #{paramCondition.createUser}


        and task.type = #{paramCondition.type}


        and task.status in

            #{state}



        and task.type in

            #{state}



        and task.process_task_id in

            #{state}



        and task.process_task_id in

            #{state}



        and (participant.user_id = #{paramCondition.participantUser}
        or task.user_id = #{paramCondition.participantUser}
        )
