mapper.select:
select
from activiti_process_task LEFT JOIN daoxin_production_pick_lists on activiti_process_task.form_id = daoxin_production_pick_lists.pick_lists_id where 1 = 1 and activiti_process_task.display = 1
and daoxin_production_pick_lists.user_id = #{paramCondition.userId}
and activiti_process_task.type = #{paramCondition.type}

select

from activiti_process_task
LEFT JOIN daoxin_task_participant ON activiti_process_task.process_task_id =
daoxin_task_participant.process_task_id
where 1 = 1 and activiti_process_task.display = 1

    and activiti_process_task.create_user = #{paramCondition.createUser}


    and activiti_process_task.type = #{paramCondition.type}


    and activiti_process_task.status in

        #{state}



    and activiti_process_task.type in

        #{state}



    and activiti_process_task.process_task_id in

        #{state}



    and (activiti_process_task.form_id in (
    select instock_order_id from daoxin_erp_instock_order where coding like
    CONCAT('%',#{paramCondition.skuName},'%')
    union
    select order_id from daoxin_anomaly_order where coding like CONCAT('%',#{paramCondition.skuName},'%')
    union
    select inventory_task_id from daoxin_erp_inventory where coding like
    CONCAT('%',#{paramCondition.skuName},'%')
    union
    select pick_lists_id from daoxin_production_pick_lists where coding like
    CONCAT('%',#{paramCondition.skuName},'%')
    ) or task_name like CONCAT('%',#{paramCondition.skuName},'%')
    or remark like CONCAT('%',#{paramCondition.skuName},'%')
    or notice_id like CONCAT('%',#{paramCondition.skuName},'%')
    )


    and activiti_process_task.process_task_id in

        #{state}



    and (daoxin_task_participant.user_id = #{paramCondition.participantUser}
    or activiti_process_task.user_id = #{paramCondition.participantUser}
    )

group by processTaskId

select
from activiti_process_task task LEFT JOIN activiti_process_log log on log.task_id = task.process_task_id LEFT JOIN activiti_steps steps on steps.setps_id = log.setps_id LEFT JOIN daoxin_task_participant participant ON task.process_task_id = participant.process_task_id where 1 = 1 and task.display = 1 -- 查询关于我的任务
and steps.step_type = "audit" and log.status = 3 and log.audit_user_id = #{paramCondition.userId}
and steps.step_type = "audit" and (log.status = 1 or log.status = 0) and log.audit_user_id = #{paramCondition.userId}
and steps.step_type = "send" and log.status = 1 and log.audit_user_id = #{paramCondition.userId}
and steps.step_type = "audit" and log.status = 3
and steps.step_type = "status" and log.status = 3
and steps.step_type = "audit" and log.status = 1
and steps.step_type = "audit" and log.status = 0
and task.status = 99
and task.status = 49
and date_format(task.create_time,'%Y-%m-%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y-%m-%d %H:%i:%s')
and date_format(task.create_time,'%Y-%m-%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y-%m-%d %H:%i:%s')
and ( task.form_id in ( select instock_order_id from daoxin_erp_instock_order where coding like CONCAT('%',#{paramCondition.skuName},'%') union select order_id from daoxin_anomaly_order where coding like CONCAT('%',#{paramCondition.skuName},'%') union select inventory_task_id from daoxin_erp_inventory where coding like CONCAT('%',#{paramCondition.skuName},'%') union select pick_lists_id from daoxin_production_pick_lists where coding like CONCAT('%',#{paramCondition.skuName},'%') ) or task_name like CONCAT('%',#{paramCondition.skuName},'%') or remark like CONCAT('%',#{paramCondition.skuName},'%') or notice_id like CONCAT('%',#{paramCondition.skuName},'%') or task.theme like CONCAT('%',#{paramCondition.skuName},'%') )
and ( task.form_id in ( select instock_order_id from daoxin_erp_instock_list where sku_id = #{paramCondition.skuId} union select inventory_id from daoxin_erp_inventory_detail where sku_id = #{paramCondition.skuId} union select pick_lists_id from daoxin_production_pick_lists_detail where sku_id = #{paramCondition.skuId} union select maintenance_id from daoxin_erp_maintenance_detail where sku_id = #{paramCondition.skuId} ) )
and task.create_user = #{paramCondition.createUser}
and task.type = #{paramCondition.type}
and task.status in
#{state}
and task.type in
#{state}
and task.process_task_id in
#{state}
and task.process_task_id in
#{state}
and (participant.user_id = #{paramCondition.participantUser} or task.user_id = #{paramCondition.participantUser} )
group by task.process_task_id

select
FROM activiti_process_task task LEFT JOIN activiti_process_log log ON log.task_id = task.process_task_id LEFT JOIN activiti_steps steps ON steps.setps_id = log.setps_id LEFT JOIN daoxin_task_participant participant ON task.process_task_id = participant.process_task_id LEFT JOIN daoxin_erp_instock_order instock on instock.instock_order_id = task.form_id LEFT JOIN daoxin_erp_instock_list instock_list on instock.instock_order_id = instock_list.instock_order_id LEFT JOIN goods_sku sku on instock_list.sku_id = sku.sku_id where 1 = 1 and task.display = 1 -- 查询关于我的任务
and steps.step_type = "audit" and log.status = 3 and log.audit_user_id = #{paramCondition.userId}
and steps.step_type = "audit" and (log.status = 1 or log.status = 0) and log.audit_user_id = #{paramCondition.userId}
and steps.step_type = "send" and log.status = 1 and log.audit_user_id = #{paramCondition.userId}
and instock.customer_id = #{paramCondition.customerId}
and steps.step_type = "audit" and log.status = 3
and steps.step_type = "status" and log.status = 3
and steps.step_type = "audit" and log.status = 1
and steps.step_type = "audit" and log.status = 0
and task.status = 99
and task.status = 49
and ( instock.coding = #{paramCondition.keyWords} or sku.sku_name like CONCAT('%',#{paramCondition.keyWords},'%') )
and date_format(task.create_time,'%Y-%m-%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y-%m-%d %H:%i:%s')
and date_format(task.create_time,'%Y-%m-%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y-%m-%d %H:%i:%s')
and task.create_user = #{paramCondition.createUser}
and task.type = #{paramCondition.type}
and task.status in
#{state}
and task.type in
#{state}
and task.process_task_id in
#{state}
and (participant.user_id = #{paramCondition.participantUser} or task.user_id = #{paramCondition.participantUser} )
group by task.process_task_id

