mapper.select:
SELECT p.order_id AS "orderId", p.new_money AS "newMoney", p.display AS "display", p.create_user AS "createUser", p.update_user AS "updateUser", p.create_time AS "createTime", p.update_time AS "updateTime", p.sp_no AS "spNo", p.STATUS AS "status", p.filed AS "filed", a.creator_user AS "creatorUser", a.msg AS msg, a.done_time As "doneTime", p.deptId AS "deptId" FROM `daoxin_crm_order_payment_apply` p LEFT JOIN wx_audit a ON p.sp_no = a.sp_no LEFT JOIN daoxin_crm_order o on p.order_id = o.order_id where 1 = 1 and p.display = 1
and o.coding = #{paramCondition.coding}
and o.theme = #{paramCondition.theme}
and p.order_id = #{paramCondition.orderId}
and p.status = #{paramCondition.status}
