mapper.select:
select
from view_order_detail detail LEFT JOIN ( SELECT DATE_FORMAT( orders.create_time, '%Y-%m' ) MONTH, DATE_FORMAT( orders.create_time, '%Y' ) YEAR, sum( record.payment_amount ) AS payment_amount FROM daoxin_crm_payment_record record LEFT JOIN daoxin_crm_order orders ON record.order_id = orders.order_id AND orders.display = 1 WHERE 1 = 1 AND record.display = 1
and orders.theme like CONCAT('%',#{paramCondition.theme},'%')
and orders.coding like CONCAT('%',#{paramCondition.coding},'%')
and orders.seller_id = #{paramCondition.sellerId}
and orders.status = #{paramCondition.status}
) payment ON detail.`YEAR` = payment.`YEAR` WHERE 1 = 1
and detail.year = #{paramCondition.year}
and date_format(detail.create_time,'%Y/%m/%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y/%m/%d %H:%i:%s')
and date_format(detail.create_time,'%Y/%m/%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y/%m/%d %H:%i:%s')
and detail.theme like CONCAT('%',#{paramCondition.theme},'%')
and detail.coding like CONCAT('%',#{paramCondition.coding},'%')
and detail.seller_id = #{paramCondition.sellerId}
and detail.status = #{paramCondition.status}

select
from daoxin_crm_order_detail orderDetail LEFT JOIN daoxin_production_plan_detail planDetail on planDetail.order_detail_id = orderDetail.detail_id LEFT JOIN daoxin_crm_order orders on orderDetail.order_id = orders.order_id WHERE ISNULL(planDetail.production_plan_detail_id)
and orders.type = #{paramCondition.type}

select * from ( SELECT remark AS "remark", detail_id AS "detailId", order_id AS "orderId", sku_id AS "skuId", brand_id AS "brandId", customer_id AS "customerId", preorde_number AS "preordeNumber", purchase_number AS "purchaseNumber", unit_id AS "unitId", one_price AS "onePrice", total_price AS "totalPrice", paper_type AS "paperType", rate AS "rate", delivery_date AS "deliveryDate", create_user AS "createUser", update_user AS "updateUser", create_time AS "createTime", update_time AS "updateTime", display AS "display", deptId AS "deptId", arrival_number AS "arrivalNumber" FROM daoxin_crm_order_detail
where 1 = 1
and
( sku_id = #{item.skuId} and customer_id = #{item.customerId}
and brand_id = #{item.brandId}
)
and detail_id like CONCAT('%',#{paramCondition.detailId},'%')
and order_id like CONCAT('%',#{paramCondition.orderId},'%')
and sku_id = #{paramCondition.skuId}
and brand_id = #{paramCondition.brandId}
and customer_id = #{paramCondition.customerId}
ORDER BY create_time DESC limit 5000 ) tmp GROUP BY tmp.skuId, tmp.brandId order by tmp.createTime DESC
