mapper.select:
SELECT sum(tableTmp.total_amount) AS "totalPrice", sum(tableTmp.payment_amount) AS "paymentPrice", sum(tableTmp.deficientPrice) AS "deficientPrice", ( SELECT COUNT( DISTINCT sku_id ) AS skuCount FROM view_order_detail where 1 = 1
and theme like CONCAT('%',#{paramCondition.theme},'%')
and coding like CONCAT('%',#{paramCondition.coding},'%')
and seller_id = #{paramCondition.sellerId}
and status = #{paramCondition.status}
and date_format(create_time,'%Y/%m/%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y/%m/%d %H:%i:%s')
and date_format(create_time,'%Y/%m/%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y/%m/%d %H:%i:%s')
) AS skuCount, ( SELECT sum( purchase_number ) AS purchaseNumber FROM view_order_detail where 1 = 1
and theme like CONCAT('%',#{paramCondition.theme},'%')
and coding like CONCAT('%',#{paramCondition.coding},'%')
and seller_id = #{paramCondition.sellerId}
and status = #{paramCondition.status}
and date_format(create_time,'%Y/%m/%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y/%m/%d %H:%i:%s')
and date_format(create_time,'%Y/%m/%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y/%m/%d %H:%i:%s')
) AS purchaseNumber, ( SELECT sum( inStock_number ) AS inStock_number FROM view_order_detail where 1 = 1
and theme like CONCAT('%',#{paramCondition.theme},'%')
and coding like CONCAT('%',#{paramCondition.coding},'%')
and seller_id = #{paramCondition.sellerId}
and status = #{paramCondition.status}
and date_format(create_time,'%Y/%m/%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y/%m/%d %H:%i:%s')
and date_format(create_time,'%Y/%m/%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y/%m/%d %H:%i:%s')
) AS inStockCount,
ROUND(( SELECT sum( inStock_number ) AS inStock_number FROM view_order_detail where 1 = 1
and theme like CONCAT('%',#{paramCondition.theme},'%')
and coding like CONCAT('%',#{paramCondition.coding},'%')
and seller_id = #{paramCondition.sellerId}
and status = #{paramCondition.status}
and date_format(create_time,'%Y/%m/%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y/%m/%d %H:%i:%s')
and date_format(create_time,'%Y/%m/%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y/%m/%d %H:%i:%s')
) /( SELECT sum( purchase_number ) AS purchaseNumber FROM view_order_detail where 1 = 1
and theme like CONCAT('%',#{paramCondition.theme},'%')
and coding like CONCAT('%',#{paramCondition.coding},'%')
and seller_id = #{paramCondition.sellerId}
and status = #{paramCondition.status}
and date_format(create_time,'%Y/%m/%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y/%m/%d %H:%i:%s')
and date_format(create_time,'%Y/%m/%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y/%m/%d %H:%i:%s')
)*100) AS inStockRate,
( SELECT COUNT( DISTINCT customer_id ) AS customerCount FROM view_order_detail where 1 = 1
and theme like CONCAT('%',#{paramCondition.theme},'%')
and coding like CONCAT('%',#{paramCondition.coding},'%')
and seller_id = #{paramCondition.sellerId}
and status = #{paramCondition.status}
and date_format(create_time,'%Y/%m/%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y/%m/%d %H:%i:%s')
and date_format(create_time,'%Y/%m/%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y/%m/%d %H:%i:%s') ) AS "sellerCount" FROM ( SELECT v.`year`, v.`month`, v.order_id, v.total_amount, p.payment_amount, (total_amount - IF ( payment_amount IS NULL, 0, payment_amount ) )AS "deficientPrice" FROM view_order_detail v LEFT JOIN ( SELECT SUM( payment_amount ) AS payment_amount, order_id AS order_id FROM daoxin_crm_payment_record WHERE 1 = 1 AND display = 1 AND `status` = 0 GROUP BY order_id ) p ON v.order_id = p.order_id where 1 = 1
and v.theme like CONCAT('%',#{paramCondition.theme},'%')
and v.coding like CONCAT('%',#{paramCondition.coding},'%')
and v.seller_id = #{paramCondition.sellerId}
and v.status = #{paramCondition.status}
and date_format(v.create_time,'%Y/%m/%d %H:%i:%s') >= str_to_date(#{paramCondition.startTime},'%Y/%m/%d %H:%i:%s')
and date_format(v.create_time,'%Y/%m/%d %H:%i:%s') <= str_to_date(#{paramCondition.endTime},'%Y/%m/%d %H:%i:%s')
and v.year = #{paramCondition.year}
GROUP BY v.order_id ORDER BY v.create_time DESC ) tableTmp

select
from daoxin_crm_order_detail orderDetail LEFT JOIN daoxin_production_plan_detail planDetail on planDetail.order_detail_id = orderDetail.detail_id LEFT JOIN daoxin_crm_order orders on orderDetail.order_id = orders.order_id WHERE ISNULL(planDetail.production_plan_detail_id)
and orders.type = #{paramCondition.type}

select * from ( SELECT remark AS "remark", detail_id AS "detailId", order_id AS "orderId", sku_id AS "skuId", brand_id AS "brandId", customer_id AS "customerId", preorde_number AS "preordeNumber", purchase_number AS "purchaseNumber", unit_id AS "unitId", one_price AS "onePrice", total_price AS "totalPrice", paper_type AS "paperType", rate AS "rate", delivery_date AS "deliveryDate", create_user AS "createUser", update_user AS "updateUser", create_time AS "createTime", update_time AS "updateTime", display AS "display", deptId AS "deptId", arrival_number AS "arrivalNumber" FROM daoxin_crm_order_detail
where 1 = 1
and
( sku_id = #{item.skuId} and customer_id = #{item.customerId}
and brand_id = #{item.brandId}
)
and detail_id like CONCAT('%',#{paramCondition.detailId},'%')
and order_id like CONCAT('%',#{paramCondition.orderId},'%')
and sku_id = #{paramCondition.skuId}
and brand_id = #{paramCondition.brandId}
and customer_id = #{paramCondition.customerId}
ORDER BY create_time DESC limit 5000 ) tmp GROUP BY tmp.skuId, tmp.brandId order by tmp.createTime DESC
