mapper.select:
select
from daoxin_crm_order where 1 = 1 and display = 1
and ( theme like CONCAT('%',#{paramCondition.keywords},'%') or coding like CONCAT('%',#{paramCondition.keywords},'%') or seller_id in ( select customer_id from daoxin_crm_customer where 1 = 1 and customer_name like CONCAT('%',#{paramCondition.keywords},'%') ) or order_id in ( select order_id from daoxin_crm_order_detail where sku_id in ( select sku_id from view_sku where 1 = 1 and ( sku_name like CONCAT('%',#{paramCondition.keywords},'%') or spu_name like CONCAT('%',#{paramCondition.keywords},'%') or category_name like CONCAT('%',#{paramCondition.keywords},'%') or standard like CONCAT('%',#{paramCondition.keywords},'%') or specifications like CONCAT('%',#{paramCondition.keywords},'%') ) )
) )
and order_id = #{paramCondition.orderId}
and contract_id = #{paramCondition.contractId}
and price like CONCAT('%',#{paramCondition.price},'%')
and buyer_id = #{paramCondition.buyerId}
and seller_id = #{paramCondition.sellerId}
and theme like CONCAT('%',#{paramCondition.theme},'%')
and origin like CONCAT('%',#{paramCondition.origin},'%')
and type like CONCAT('%',#{paramCondition.type},'%')
and generate_contract like CONCAT('%',#{paramCondition.generateContract},'%')
and delivery_date like CONCAT('%',#{paramCondition.deliveryDate},'%')
and coding like CONCAT('%',#{paramCondition.coding},'%')
and seller_id in ( select customer_id from daoxin_crm_customer where customer_name like CONCAT('%',#{paramCondition.customerName},'%') )
and order_id in ( select order_id from daoxin_crm_order_detail where sku_id in ( select sku_id from goods_sku where sku_name like CONCAT('%',#{paramCondition.skuName},'%') union select sku_id from goods_sku where standard like CONCAT('%',#{paramCondition.skuName},'%') union select sku_id from goods_sku where spu_id in (select spu_id from goods_spu where name like CONCAT('%',#{paramCondition.skuName},'%') ) ) ) or buyer_id in (select customer_id from daoxin_crm_customer where customer_name like #{paramCondition.skuName} and display = 1 ) or coding like #{paramCondition.skuName}
